import React, {useContext, useEffect, useState,useRef} from 'react'
import AuthContext from './AuthContext'
import useMediaQuery from '@material-ui/core/useMediaQuery';
import {fetchAllMasks} from './actions'
import {Link,withRouter} from 'react-router-dom'
import { makeStyles } from '@material-ui/core/styles';
import CustomButton from './CustomButton/CustomButton'

// reactstrap components
import {
  Button,
  Card,
  CardHeader,
  CardBody,
  CardFooter,
  CardTitle,
  CardText,
  Collapse,
  Label,
  FormGroup,
  Input,
  Container,
  Row,
  Col,
  UncontrolledTooltip,
} from "reactstrap";

const App = ({history, match}) => {

    const {state,dispatch} = useContext(AuthContext)
    const {allMasks,cartItems,hitCount} = state;

    let sessionCartItems = JSON.parse(sessionStorage.getItem('cart'))
    let sessionAllMasks = JSON.parse(sessionStorage.getItem('allMasks'))

    const [filterValue, setFilterValue] = useState({people:false,animals:false,flowers:false})
    const mobileSize = useMediaQuery('(max-width:600px)');
    const tabletSize = useMediaQuery('(max-width:800px)');

    const itemsRef = useRef([]);

    const useStyles = makeStyles(theme => ({
        paper: {
          display: 'flex',
          textAlign: 'center',
          flexDirection: 'column',
          padding:'15px',
          margin:'15px',
        },
        avatar: {
          margin: theme.spacing(1),
          backgroundColor: theme.palette.secondary.main,
        },
        form: {
          width: '100%', // Fix IE 11 issue.
          marginTop: theme.spacing(3),
        },
        submit: {
          margin: theme.spacing(3, 0, 2),
        },
        paper: {
          padding: theme.spacing(.25),
          textAlign: 'center',
          color: theme.palette.text.secondary,
        },
        clickable:{
          cursor:'pointer',
          width:'200px',
          height:'160px',
          objectFit:'cover',
          borderRadius:'4x',
        },
        scroll:{
          overflow:'scroll',
          margin:'15% 0 0 0'
        },
        width:{
          width:0
        },
        center:{
          textAlign:'center'
        },
        buttonCenter:{
          margin:'5px',
          cursor:'pointer'
        },
        fullWidth:{
          margin:'auto'
        },
        buttonMargin:{
          marginRight:'10px'
        },
        soldOut:{
          color:'red',
          fontWeight:'bold',
          margin:'0 0 15% 0'
        },
        section:{
          margin:'10% 0 15% 0'
        },
        quantityStyle:{
          margin:'0 0 15% 0'
        },
        header:{
          width:'100%',
          textAlign:'center',
          fontWeight:'bold',
          fontSize:'1.05em',
          display:'flex',
          flexDirection:'row'
        },
        innerHeader:{
          width:'30%',
        },
        mobileInnerHeader:{
          width:'17%',
        },
        outerHeader:{
          width:'65%',
        }
      }));

    const classes = useStyles();
    useEffect(() =>{
      let maskType;

      fetchAllMasks(dispatch,match.params.category)

        if(sessionAllMasks && sessionAllMasks.length > 1 && sessionAllMasks !== null){
          console.log("")
        } else{
          sessionStorage.setItem('allMasks',JSON.stringify(allMasks))
        }
    },[])


    useEffect(() =>{

      if(sessionAllMasks && sessionAllMasks.length > 1 && sessionAllMasks !== null){
        console.log("")
      } else{
        sessionStorage.setItem('allMasks',JSON.stringify(allMasks))
      }


      if(cartItems && cartItems.length > 0){
        // try{
          if(cartItems !== undefined || cartItems !== null ){
            sessionStorage.setItem('cart',JSON.stringify(cartItems))
        } else{
            sessionStorage.setItem('cart',[])
          }


            newTotal = cartItems.reduce((acc,cartItem) => acc + cartItem.quantity,0)
            sessionStorage.setItem('cartTotal',newTotal)

          console.log("sessionCartItems")
          console.log(sessionCartItems)
          console.log("cartItems")
          console.log(cartItems)

          newTotal = cartItems.reduce((acc,cartItem) => acc + cartItem.quantity,0)

    }
    },[cartItems,hitCount])

    // collapse states and functions
    const [collapses, setCollapses] = React.useState([1]);
    const changeCollapse = (collapse) => {
      if (collapses.includes(collapse)) {
        setCollapses(collapses.filter((prop) => prop !== collapse));
      } else {
        setCollapses([...collapses, collapse]);
      }
    };


    const filterMasks = e =>{
      history.push(`/${e.target.value}`)
      fetchAllMasks(dispatch,match.params.category)
    }


    let newTotal;

    return (
        <>
      <div className={"wrapper",classes.scroll}>
        <div className={"main"}>
          <div className={"section"}>
            <Container>
              <Row>
              {tabletSize || mobileSize ? 
                (<div className={classes.header}>
                  <div className={classes.mobileInnerHeader}></div>
                  <div className={classes.outerHeader}>
                    <p>Filter Mask by Type</p>
                  </div>
                </div>) : null}
                {mobileSize || tabletSize ? (
                <div className={classes.fullWidth}>
                  <Button color={filterValue["animals"] == true ? "success" : "info"} type="button" className={classes.buttonCenter} value="animals"  onClick={e => filterMasks(e)}>
                      Animals
                  </Button>
                  <Button color={filterValue["flowers"] == true ? "success" : "info"} type="button" className={classes.buttonCenter} value="flowers"  onClick={e => filterMasks(e)}>
                      Flowers
                  </Button>
                  <Button color={filterValue["people"] == true ? "success" : "info"} type="button" className={classes.buttonCenter} value="people"  onClick={e => filterMasks(e)}>
                      People
                  </Button>
                </div>
                ):(
                  <Col md="3">
                  {!mobileSize ? (<Button color="info" type="button"><Link to="/checkout">Go To Checkout</Link></Button>) : null}
                  <div className="collapse-panel">
                    <CardBody>
                      <Card className="card-refine card-plain">
                      <CardTitle tag="h4">
                          Filter Mask by Type{" "}
                          <Button
                            className="btn-icon btn-neutral pull-right"
                            color="default"
                            id="tooltip633919451"
                          >
                            <i className="arrows-1_refresh-69 now-ui-icons"></i>
                          </Button>
                          <UncontrolledTooltip
                            delay={0}
                            target="tooltip633919451"
                          >
                            Reset Filter
                          </UncontrolledTooltip>
                        </CardTitle>
                        <CardHeader id="headingTwo" role="tab">
                          <h6>
                            <a
                              className="text-info"
                              aria-expanded={collapses.includes(1)}
                              data-toggle="collapse"
                              data-parent="#accordion"
                              href="#pablo"
                              onClick={(e) => {
                                e.preventDefault();
                                changeCollapse(1);
                              }}
                            >
                              Type{" "}
                              <i className="now-ui-icons arrows-1_minimal-down"></i>
                            </a>
                          </h6>
                        </CardHeader>
                      </Card>
                    </CardBody>
                  </div>
                </Col>
                )}
            <Col md="9">
                <Row>
            {allMasks && allMasks.map((post,i) =>{
                const id = post._id;
                const title = post.title;
                const price = post.price;
                const url = post.url;
                const quantity = post.quantity;
                let sessionCartItems = JSON.parse(sessionStorage.getItem('cart'))
                let qty;
                let limit;
                let disabled;
                let currentItem = itemsRef.current[i]

                return(
                <>
                    <Col lg="4" md="6">
                      <Card className="card-product card-plain">
                      <div className={"card-image",classes.center}>
                          <a onClick={(e) => history.push(`/product-page/${post._id}`)}>
                            <img
                              className={classes.clickable}
                              alt="..."
                              src={post.url}
                            />
                          </a>
