import React,{useState,useContext,useEffect} from 'react'
import useMediaQuery from '@material-ui/core/useMediaQuery';
import AuthContext from '../AuthContext'
import AppBar from "@material-ui/core/AppBar";
import {withRouter} from 'react-router-dom'
import { makeStyles } from '@material-ui/core/styles';
import Toolbar from "@material-ui/core/Toolbar";
import Typography from "@material-ui/core/Typography";
import Button from '@material-ui/core/Button';
import ShoppingCartIcon from '@material-ui/icons/ShoppingCart';
import CartDropdown from '../CartDropdown/CartDropdown'
// import { useToasts } from 'react-toast-notifications'
import {Link} from 'react-router-dom'

const Header = ({history}) => {
    const {state,dispatch} = useContext(AuthContext)
    const {cartItems,toggleCart,hitItem,allMasks} = state;
    // const { addToast } = useToasts()
    let cartAlerts = JSON.parse(sessionStorage.getItem('orderNotification'))
    let cartItemCount = sessionStorage.getItem('cartTotal')
    let orderCountItems = sessionStorage.getItem('orderCount')
    let sessionCartItems = JSON.parse(sessionStorage.getItem('cart'))
    let newTotal;

    useEffect(() =>{              
        sessionCartItems = JSON.parse(sessionStorage.getItem('cart'))
        cartItemCount = sessionStorage.getItem('cartTotal')

        if(cartItemCount == null){
            sessionStorage.setItem('cartTotal',0)
        } 
            // sessionStorage.setItem(`${id}`,[{...foundItem,hitCount:1}])


    },[cartItems,hitItem])



        const mobileSize = useMediaQuery('(max-width:600px)');
        const tabletSize = useMediaQuery('(max-width:800px)');

const handleCartClick = () =>{
    dispatch({type:"TOGGLE_CART",payload:toggleCart})
}

const headerStyle = {
    width:'100%',
    height:'175px',
    display: 'flex',
    justifyContent: 'spaceBetween'
}

const mobHeaderStyle = {
    width:'100%',
    height:'105px',
    display: 'flex',
    justifyContent: 'spaceBetween'
}

const iconStyle = {
    width:'85%',
    height:'175px'
}
let cartSectionStyle = {
    width:'10%',
    height:'100%',
    display: 'tableCell',
    verticalAlign: 'bottom',
    padding:'10px'
}
let mobCartSectionStyle = {
    padding:'0px'
}
let mobcartSectionStyle = {
    padding: '0px',
    width:'0px'
}
let mobButtonStyle = {
    height:'25',
    width:'25',
    float:'right'
}
let mobFontStyle ={
    fontSize: '10px'
}

let buttonStyle = {
    height:'50',
    width:'50'
}

let fontStyle = {
    fontSize:'14px'
}

    const useStyles = makeStyles(theme => ({
        root: {
          flexGrow: 1
        },
        grow: {
          flexGrow: 1,
          display: "flex",
          alignItems: "center"
        },
        icon: {
          color: "green",
          fontSize: 45
        },
        mobile: {
          display: "none"
        },
        picture: {
          height: "50px",
          borderRadius: "90%"
        },
        menuButton: {
          marginRight: theme.spacing(2),
        },
        title: {
            flexGrow: 1,
          },
        alertStyle:{
            backgroundColor:'red',
            width:'5%',
            borderRadius:'55px',
            textAlign:'center'
        },
        appbarHeight:{
            marginTop:'88vh',
            height:'50px'
        },
        appbarInnerDiv:{
            width:'50%',
        },
        appbarOuterDiv:{
            width:'50%',
            float:'right',
        },
        appbarHeightDiv:{
            width:'100%',
            margin:'auto',
            display: 'flex',
            justifyContent: 'spaceBetween'
        },
        mobileMenuButton:{
            marginTop:'90vh',
            height:'50px',
            textAlgin:'center'
        },
        bigFont:{
            fontSize:'26px'
        },
        center:{
            margin:'auto'
        }
      })
    );

    const clickable = {
        cursor:'pointer',
        margin: '0 25px 0 0'
    }

    if(cartItems && cartItems.length > 0){
        try{

            sessionStorage.setItem('cart',JSON.stringify(cartItems))
            newTotal = cartItems.reduce((acc,cartItem) => acc + cartItem.quantity,0)
            sessionStorage.setItem('cartTotal',newTotal)

        } catch(e){
            console.log("cartItems is empty")
        }
    }

    const classes = useStyles();

    const showNotification = () =>{
        // addToast('An order was created', { appearance: 'success' }, () => console.log("toast shown"))
    }

    const showAlerts = () =>{
        // dispatch({type:"TOGGLE_ALERT_DROPDOWN",payload:!toggleAlertDropDown})
    }

    return (
        <div className={classes.root}>
         <AppBar color="teal" position="fixed">
         {mobileSize ? 
         (<>
            <AppBar className={classes.appbarHeight} position="fixed" color="info" type="button"><Link to="/checkout" className={classes.center}><p className={classes.bigFont}>Go To Checkout</p></Link>
                <div className={classes.appbarHeightDiv}>
                    <div className={classes.appbarInnerDiv}>
                        <ShoppingCartIcon style={mobButtonStyle} />
                    </div>
                    <div className={classes.appbarOuterDiv}>
                        <p style={fontStyle}>
                            Cart Items: {cartItemCount}
                        </p>
                    </div>
                </div>
            </AppBar>
            </>) 
         : null}

                <div style={mobileSize ? mobHeaderStyle: headerStyle}>
                    <div style={iconStyle}>
                        <a  onClick={() => history.push('/')} style={clickable}><img src="https://res.cloudinary.com/dzdvrgbjd/image/upload/v1599961709/CAAEYC_SouthernCalifornia_satellite_wide_tcqkpt.png" /></a>
                    </div>
                            <div>{orderCountItems}</div>
                            {!mobileSize ? 
                    (<div style={mobileSize ? mobCartSectionStyle : cartSectionStyle}>
                        <Button edge="start" onClick={handleCartClick} className={classes.menuButton} color="inherit">
                            <ShoppingCartIcon style={buttonStyle} />
                        </Button>
                                <p style={fontStyle}>Cart Items: {cartItemCount}</p>
                        {toggleCart ? (<CartDropdown cartItems={sessionCartItems} />):null}
                    </div>)
                        :null}
                </div>  

        </AppBar>
    </div>
    )        
}

export default Header;
